function hideSelectionHint(a){var b=$("#selectionHint");if(a){b.addClass("hidden")}else{b.removeClass("hidden")}}function hideSpinner(a){var b=$("#spinner");if(a){b.addClass("hidden")}else{b.removeClass("hidden")}}function hideTranslationRow(a){var b=$("#translationRow");if(a){b.addClass("hidden");$("translationTableBody").empty()}else{b.removeClass("hidden")}}function setSelectionNeededState(){hideSelectionHint(false);hideSpinner(true);hideTranslationRow(true)}$("#app").on("changed.bs.select",function(e,d,f,a){setSelectionNeededState();var c=$("#resource");$("option",c).remove();if(f){var b=$(this).val();if(b in resources){$.each(resources[b],function(g,h){c.append('<option value="'+g+'">'+h+"</option>")})}}c.selectpicker("refresh")});$("#resource").on("changed.bs.select",function(c,b,e,a){if(e){hideSelectionHint(true);hideSpinner(false);hideTranslationRow(true);var f=$("#app").val();var d=$(this).val();$.ajax({url:translationsPath+"/app/"+f+"/resource/"+d,dataType:"html",method:"GET"}).done(function(g){$("translationTableBody").html(g);hideSelectionHint(true);hideSpinner(true);hideTranslationRow(false)}).fail(function(g,i,h){setSelectionNeededState()})}else{setSelectionNeededState()}});